package cn.com.hgh.utils;/** * 常用正则验证 * @author shefenfei * @date 2014-09-19 * @version 3.0 */public class REGX {	/**手机号码输入匹配正则*/	public static final String REGX_MOBILE = "^1$|^1[34578]$|^1[3-8]\\d{0,9}$";	/**手机号码提交验证正则*/	public static final String REGX_MOBILE_FINAL = "^1[34578]\\d{9}$";		/**固定电话输入匹配正则*/	public static final String REGX_PHONE = "^0\\d{0,1}$|^0\\d{2,3}-?$|^0\\d{2,3}-?([1-9]\\d{0,4})?$|^0\\d{2,3}-?[1-9]\\d{5,7}|0\\d{2,3}-?([1-9]\\d{5,7})?$";	/**固定电话提交验证正则*/	public static final String REGX_PHONE_FINAL = "^0\\d{2,3}-?[1-9]\\d{5,7}$";		/**折扣数提交验证*/	public static final String REGX_DISCOUNT_FINAL = "^\\d(.\\d)?$";		/**折扣数正则*/	public static final String REGX_DISCOUNT = "^[1-9]$|^[1-9]([\\.]\\d{0,1})$|^[0]([\\.]\\d{0,1})?$";		/**匹配数字 */	public static final String REGX_NUM = "[0-9]{15}";}